import React from 'react';

// promise : 
//     resolve 
//     reject
//     pending


function myFun(){
    return new Promise(function(resolve,reject){
        setTimeout(()=>{
            console.log("promise resolved")
            resolve();
        },2000)
    })
}

export default Promise
