import React from 'react';

class Hello extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            // name : "tusharr",
            // lastName : "lohana",
            // salary : this.props.salary
            date : new Date()
        }
    }
    componentDidMount(){
        this.int = setInterval(() => this.clock(),1000 )
    }
    componentDidUnmount(){
        clearInterval(this.int)
    }

    clock(){
        this.setstate({
            date : new Date()
        })
    }
    render(){
        return(
            <h1>date is  { this.state.date.toLocaleTimeString()}</h1>
            // <h1> this is a class {this.state.name } {this.state.lastName} {this.state.salary} </h1>
        )
    }
}

export default Hello
